def create_folder_content(vault_path):
    """Create content for all empty folders"""
    created_files = []
    
    for folder_path, files in FOLDER_CONTENT.items():
        full_folder_path = Path(vault_path) / folder_path
        
        # Create folder if it doesn't exist
        full_folder_path.mkdir(parents=True, exist_ok=True)
        
        # Create each file
        for filename, content in files:
            file_path = full_folder_path / filename
            
            # Only create if doesn't exist
            if not file_path.exists():
                with open(file_path, 'w', encoding='utf-8') as f:
                    f.write(content)
                created_files.append(str(file_path))
                print(f"âœ… Created: {file_path.name}")
    
    return created_files

